    private void txtValiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_txtValiActionPerformed
        String checkname = txtName.getText();
        String checkAd = txtAddress.getText();
        String checkSex = txtMale.getText();
        String checkFe = txtfemale.getText();
        //String checkQua = txtCobo.getText();
        String checkHob = txtReading.getText();
        String checkSing = txtSingning.getText();
        String checkDacing = txtDancing.getText();
        String checlVali = txtVali.getText();
        String checkRs = txtRs.getText();

        if (checkname.equals("")) {
            JOptionPane.showMessageDialog(this, "Vui lòng nhập tên");
            return;
        }
        if (checkAd.equals("")) {
            JOptionPane.showMessageDialog(this, "Vui lòng nhập Address");
            return;
        }
        if (checkSex.equals("") || checkFe.equals("")) {
            JOptionPane.showMessageDialog(this, "Vui lòng nhập giới tính");
            return;
        }

        if (checkHob.equals("") || checkSing.equals("") || checkDacing.equals("")) {
            JOptionPane.showMessageDialog(this, "Vui lòng nhập Hobby");
            return;
        }


    }//GEN-LAST:event_txtValiActionPerformed

